import React, { useState, useEffect } from 'react';
import { Calendar, Clock, Users, Bell, ChevronRight, Plus, X, Check, Search, Filter, Edit, Trash2, MapPin } from 'lucide-react';
import { useNavigate } from 'react-router-dom';
import { io } from 'socket.io-client';

const DigitalClock = () => {
  const [time, setTime] = useState(new Date());

  useEffect(() => {
    const timer = setInterval(() => setTime(new Date()), 1000);
    return () => clearInterval(timer);
  }, []);

  return (
    <div className="fixed top-4 left-4 bg-white/90 backdrop-blur-sm p-4 rounded-lg shadow-lg z-50">
      <div className="text-3xl font-mono font-bold text-blue-600">
        {time.toLocaleTimeString('en-US', {
          hour12: true,
          hour: '2-digit',
          minute: '2-digit',
          second: '2-digit'
        })}
      </div>
      <div className="text-sm text-gray-500 mt-1">
        {time.toLocaleDateString('en-US', {
          weekday: 'long',
          year: 'numeric',
          month: 'long',
          day: 'numeric'
        })}
      </div>
    </div>
  );
};

const EventDashboard = () => {
  const [events, setEvents] = useState([]);
  const [showCreateModal, setShowCreateModal] = useState(false);
  const [notifications, setNotifications] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [filterStatus, setFilterStatus] = useState('all');
  const [selectedEvent, setSelectedEvent] = useState(null);
  const [newEvent, setNewEvent] = useState({
    title: '',
    date: '',
    time: '',
    location: '',
    description: '',
    attendees: ''
  });
  const [socket, setSocket] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const newSocket = io('http://localhost:4000');
    setSocket(newSocket);

    newSocket.on('eventNotification', (notification) => {
      setNotifications(prev => [...prev, { ...notification, id: Date.now() }]);
      setTimeout(() => {
        setNotifications(prev => prev.filter(n => n.id !== notification.id));
      }, 5000);
    });

    newSocket.on('eventUpdated', (updatedEvent) => {
      setEvents(prev => prev.map(event =>
        event.id === updatedEvent.id ? updatedEvent : event
      ));
    });

    newSocket.on('eventCreated', (newEvent) => {
      setEvents(prev => [...prev, newEvent]);
    });

    return () => newSocket.disconnect();
  }, []);

  const handleCreateEvent = () => {
    if (socket) {
      const eventData = {
        ...newEvent,
        id: Date.now(),
        attendees: newEvent.attendees.split(',').map(email => ({
          id: Date.now() + Math.random(),
          email: email.trim(),
          status: 'pending'
        })),
        notifications: [],
        status: 'upcoming'
      };
      socket.emit('createEvent', eventData);
      setShowCreateModal(false);
      setNewEvent({
        title: '',
        date: '',
        time: '',
        location: '',
        description: '',
        attendees: ''
      });
    }
  };

  const handleDeleteEvent = (eventId) => {
    if (socket) {
      socket.emit('deleteEvent', eventId);
      setEvents(prev => prev.filter(event => event.id !== eventId));
    }
  };

  const filteredEvents = events
    .filter(event => event.title.toLowerCase().includes(searchTerm.toLowerCase()))
    .filter(event => filterStatus === 'all' || event.status === filterStatus);

  return (
    <div className="min-h-screen bg-gray-50">
      <DigitalClock />

      <div className="p-6 max-w-6xl mx-auto relative">
        {/* Header with Search and Filters */}
        <div className="mb-8">
          <div className="flex justify-between items-center mb-6">
            <div>
              <h1 className="text-4xl font-bold mb-2 text-gray-800">EventSync Dashboard</h1>
              <p className="text-gray-600">Manage your upcoming events and attendees</p>
            </div>
            <button
              onClick={() => setShowCreateModal(true)}
              className="bg-blue-600 text-white px-6 py-3 rounded-lg flex items-center gap-2 hover:bg-blue-700 transition-colors shadow-md"
            >
              <Plus className="h-5 w-5" />
              Create Event
            </button>
          </div>

          <div className="flex gap-4 items-center bg-white p-4 rounded-lg shadow-md">
            <div className="flex-1 relative">
              <Search className="h-5 w-5 absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" />
              <input
                type="text"
                placeholder="Search events..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="w-full pl-10 pr-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              />
            </div>
            <div className="flex items-center gap-2">
              <Filter className="h-5 w-5 text-gray-500" />
              <select
                value={filterStatus}
                onChange={(e) => setFilterStatus(e.target.value)}
                className="border p-2 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              >
                <option value="all">All Events</option>
                <option value="upcoming">Upcoming</option>
                <option value="ongoing">Ongoing</option>
                <option value="completed">Completed</option>
              </select>
            </div>
          </div>
        </div>

        {/* Notifications */}
        <div className="fixed top-20 right-4 w-96 space-y-2 z-50">
          {notifications.map((notification) => (
            <div
              key={notification.id}
              className="bg-white p-4 rounded-lg shadow-lg flex items-center justify-between transform transition-all duration-500 hover:scale-102"
            >
              <div className="flex items-center gap-2">
                <Bell className="h-5 w-5 text-blue-500" />
                <span className="text-gray-700">{notification.message}</span>
              </div>
              <button
                onClick={() => setNotifications(prev => prev.filter(n => n.id !== notification.id))}
                className="text-gray-400 hover:text-gray-600"
              >
                <X className="h-4 w-4" />
              </button>
            </div>
          ))}
        </div>

        {/* Events Grid */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          {filteredEvents.map(event => (
            <div
              key={event.id}
              className="bg-white rounded-xl shadow-md hover:shadow-xl transition-shadow overflow-hidden"
            >
              <div className="p-6">
                <div className="flex justify-between items-start mb-4">
                  <h3 className="text-xl font-semibold text-gray-800">{event.title}</h3>
                  <div className="flex gap-2">
                    <button
                      onClick={() => setSelectedEvent(event)}
                      className="p-2 hover:bg-gray-100 rounded-full transition-colors"
                    >
                      <Edit className="h-5 w-5 text-gray-500" />
                    </button>
                    <button
                      onClick={() => handleDeleteEvent(event.id)}
                      className="p-2 hover:bg-red-100 rounded-full transition-colors"
                    >
                      <Trash2 className="h-5 w-5 text-red-500" />
                    </button>
                  </div>
                </div>

                <div className="grid grid-cols-2 gap-4 mb-4">
                  <div className="flex items-center gap-2 text-gray-600">
                    <Calendar className="h-5 w-5" />
                    <span>{event.date}</span>
                  </div>
                  <div className="flex items-center gap-2 text-gray-600">
                    <Clock className="h-5 w-5" />
                    <span>{event.time}</span>
                  </div>
                  {event.location && (
                    <div className="flex items-center gap-2 text-gray-600 col-span-2">
                      <MapPin className="h-5 w-5" />
                      <span>{event.location}</span>
                    </div>
                  )}
                </div>

                <div className="mb-4">
                  <h4 className="font-semibold text-gray-700 mb-2">Attendees ({event.attendees.length})</h4>
                  <div className="flex flex-wrap gap-2">
                    {event.attendees.map(attendee => (
                      <span
                        key={attendee.id}
                        className={`px-3 py-1 rounded-full text-sm ${attendee.status === 'confirmed' ? 'bg-green-100 text-green-800' :
                            attendee.status === 'declined' ? 'bg-red-100 text-red-800' :
                              'bg-yellow-100 text-yellow-800'
                          }`}
                      >
                        {attendee.email}
                      </span>
                    ))}
                  </div>
                </div>

                <button
                  onClick={() => navigate(`/event/${event.id}`)}
                  className="w-full bg-gray-50 text-gray-700 py-2 rounded-lg hover:bg-gray-100 transition-colors flex items-center justify-center gap-2"
                >
                  View Details
                  <ChevronRight className="h-4 w-4" />
                </button>
              </div>
            </div>
          ))}
        </div>

        {/* Create/Edit Modal */}
        {(showCreateModal || selectedEvent) && (
          <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
            <div className="bg-white p-6 rounded-xl w-full max-w-md">
              <div className="flex justify-between items-center mb-6">
                <h2 className="text-2xl font-bold text-gray-800">
                  {selectedEvent ? 'Edit Event' : 'Create New Event'}
                </h2>
                <button
                  onClick={() => {
                    setShowCreateModal(false);
                    setSelectedEvent(null);
                  }}
                  className="text-gray-400 hover:text-gray-600"
                >
                  <X className="h-6 w-6" />
                </button>
              </div>

              <div className="space-y-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">Event Title</label>
                  <input
                    type="text"
                    value={selectedEvent?.title || newEvent.title}
                    onChange={(e) => selectedEvent
                      ? setSelectedEvent({ ...selectedEvent, title: e.target.value })
                      : setNewEvent({ ...newEvent, title: e.target.value })
                    }
                    className="w-full p-2 border rounded-lg focus:ring-2 focus:ring-blue-500 focus:outline-none"
                    placeholder="Enter event title"
                  />
                </div>

                <div className="grid grid-cols-2 gap-4">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">Date</label>
                    <input
                      type="date"
                      value={selectedEvent?.date || newEvent.date}
                      onChange={(e) => selectedEvent
                        ? setSelectedEvent({ ...selectedEvent, date: e.target.value })
                        : setNewEvent({ ...newEvent, date: e.target.value })
                      }
                      className="w-full p-2 border rounded-lg focus:ring-2 focus:ring-blue-500 focus:outline-none"
                    />
                  </div>
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">Time</label>
                    <input
                      type="time"
                      value={selectedEvent?.time || newEvent.time}
                      onChange={(e) => selectedEvent
                        ? setSelectedEvent({ ...selectedEvent, time: e.target.value })
                        : setNewEvent({ ...newEvent, time: e.target.value })
                      }
                      className="w-full p-2 border rounded-lg focus:ring-2 focus:ring-blue-500 focus:outline-none"
                    />
                  </div>
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">Location</label>
                  <input
                    type="text"
                    value={selectedEvent?.location || newEvent.location}
                    onChange={(e) => selectedEvent
                      ? setSelectedEvent({ ...selectedEvent, location: e.target.value })
                      : setNewEvent({ ...newEvent, location: e.target.value })
                    }
                    className="w-full p-2 border rounded-lg focus:ring-2 focus:ring-blue-500 focus:outline-none"
                    placeholder="Enter location"
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">Description</label>
                  <textarea
                    value={selectedEvent?.description || newEvent.description}
                    onChange={(e) => selectedEvent
                      ? setSelectedEvent({ ...selectedEvent, description: e.target.value })
                      : setNewEvent({ ...newEvent, description: e.target.value })
                    }
                    className="w-full p-2 border rounded-lg focus:ring-2 focus:ring-blue-500 focus:outline-none"
                    placeholder="Enter event description"
                    rows={3}
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Attendees (comma-separated emails)
                  </label>
                  <textarea
                    value={selectedEvent?.attendees.map(a => a.email).join(', ') || newEvent.attendees}
                    onChange={(e) =>
                      selectedEvent
                        ? setSelectedEvent({
                          ...selectedEvent,
                          attendees: e.target.value.split(',').map(email => ({
                            id: Date.now() + Math.random(),
                            email: email.trim(),
                            status: 'pending',
                          }))
                        })
                        : setNewEvent({ ...newEvent, attendees: e.target.value })
                    }
                    className="w-full p-2 border rounded-lg focus:ring-2 focus:ring-blue-500 focus:outline-none"
                    placeholder="email1@example.com, email2@example.com"
                    rows={3}
                  />

                </div>

                <div className="flex gap-4 pt-4">
                  <button
                    onClick={() => {
                      if (selectedEvent) {
                        socket.emit('updateEvent', selectedEvent);
                        setSelectedEvent(null);
                      } else {
                        handleCreateEvent();
                      }
                    }}
                    className="flex-1 bg-blue-600 text-white py-2 rounded-lg hover:bg-blue-700 transition-colors"
                  >
                    {selectedEvent ? 'Save Changes' : 'Create Event'}
                  </button>
                  <button
                    onClick={() => {
                      setShowCreateModal(false);
                      setSelectedEvent(null);
                    }}
                    className="flex-1 bg-gray-100 text-gray-700 py-2 rounded-lg hover:bg-gray-200 transition-colors"
                  >
                    Cancel
                  </button>
                </div>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default EventDashboard;
