import React, { useState, useEffect } from 'react';
import { Calendar, Clock, Users, Bell, ChevronRight } from 'lucide-react';
import { useNavigate } from 'react-router-dom';
import { io } from 'socket.io-client';

const EventDashboard = () => {
  const history = useHistory();
  const [events, setEvents] = useState([
    {
      id: 1,
      title: 'Team Planning Session',
      date: '2025-01-10',
      time: '14:00',
      attendees: [
        { id: 1, name: 'Sarah Chen', status: 'confirmed' },
        { id: 2, name: 'Mike Johnson', status: 'pending' },
        { id: 3, name: 'Alex Kim', status: 'confirmed' }
      ],
      notifications: [
        { id: 1, message: 'Mike Johnson has not responded yet', type: 'warning' },
        { id: 2, message: 'Sarah Chen confirmed attendance', type: 'success' }
      ]
    },
    {
      id: 2,
      title: 'Product Launch Meeting',
      date: '2025-01-15',
      time: '10:00',
      attendees: [
        { id: 4, name: 'Emma Wilson', status: 'confirmed' },
        { id: 5, name: 'James Smith', status: 'confirmed' }
      ],
      notifications: [
        { id: 3, message: 'All attendees confirmed', type: 'success' }
      ]
    }
  ]);

  const [notifications, setNotifications] = useState([]);

  useEffect(() => {
    const socket = io('http://localhost:4000'); // Connect to the Node.js server

    socket.on('eventNotification', (data) => {
      setNotifications((prevNotifications) => [
        ...prevNotifications,
        data
      ]);
    });

    return () => {
      socket.disconnect();
    };
  }, []);

  const navigateToEventDetail = (eventId) => {
    history.push(`/events/${eventId}`);
  };

  return (
    <div className="p-6 max-w-6xl mx-auto">
      <div className="mb-8">
        <h1 className="text-3xl font-bold mb-2">EventSync Dashboard</h1>
        <p className="text-gray-600">Manage your upcoming events and attendees</p>
      </div>

      {/* Notifications Section */}
      <div className="mb-6">
        {notifications.map((notification, index) => (
          <div key={index} className="alert alert-info">
            <Bell className="h-4 w-4" />
            <span>{notification.message}</span>
          </div>
        ))}
      </div>

      {/* Events List */}
      <div className="grid gap-4">
        {events.map((event) => (
          <div key={event.id} className="event-card">
            <div className="flex justify-between">
              <h3>{event.title}</h3>
              <button onClick={() => navigateToEventDetail(event.id)}>
                <ChevronRight />
              </button>
            </div>
            <p>{event.date} - {event.time}</p>
            <p>{event.attendees.length} attendees</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default EventDashboard;
