import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { Calendar, Clock, Users, Bell, ChevronRight } from 'lucide-react';
import { Alert, AlertDescription } from '@/components/ui/alert';

const EventDashboard = () => {
  const navigate = useNavigate();
  const [events, setEvents] = useState([
    {
      id: 1,
      title: 'Team Planning Session',
      date: '2025-01-10',
      time: '14:00',
      attendees: [
        { id: 1, name: 'Sarah Chen', status: 'confirmed' },
        { id: 2, name: 'Mike Johnson', status: 'pending' },
        { id: 3, name: 'Alex Kim', status: 'confirmed' }
      ],
      notifications: [
        { id: 1, message: 'Mike Johnson has not responded yet', type: 'warning' },
        { id: 2, message: 'Sarah Chen confirmed attendance', type: 'success' }
      ]
    },
    {
      id: 2,
      title: 'Product Launch Meeting',
      date: '2025-01-15',
      time: '10:00',
      attendees: [
        { id: 4, name: 'Emma Wilson', status: 'confirmed' },
        { id: 5, name: 'James Smith', status: 'confirmed' }
      ],
      notifications: [
        { id: 3, message: 'All attendees confirmed', type: 'success' }
      ]
    }
  ]);

  return (
    <div className="p-6 max-w-6xl mx-auto">
      <div className="mb-8">
        <h1 className="text-3xl font-bold mb-2">EventSync Dashboard</h1>
        <p className="text-gray-600">Manage your upcoming events and attendees</p>
      </div>

      {/* Notifications Section */}
      <div className="mb-6">
        {events.flatMap(event => 
          event.notifications.map(notification => (
            <Alert key={notification.id} className={`mb-2 ${
              notification.type === 'warning' ? 'bg-yellow-50' : 'bg-green-50'
            }`}>
              <Bell className="h-4 w-4" />
              <AlertDescription>
                {event.title}: {notification.message}
              </AlertDescription>
            </Alert>
          ))
        )}
      </div>

      {/* Events List */}
      <div className="grid gap-4">
        {events.map(event => (
          <div key={event.id} className="hover:shadow-lg transition-shadow">
            <div className="flex flex-row items-center justify-between">
              <h2 className="text-xl">{event.title}</h2>
              <button 
                className="p-2 hover:bg-gray-100 rounded-full"
                onClick={() => navigate(`/events/${event.id}`)}
              >
                <ChevronRight className="h-5 w-5" />
              </button>
            </div>
            <div>
              <div className="flex items-center gap-2">
                <Calendar className="h-5 w-5 text-gray-500" />
                <span>{event.date}</span>
              </div>
              <div className="flex items-center gap-2">
                <Clock className="h-5 w-5 text-gray-500" />
                <span>{event.time}</span>
              </div>
              <div className="flex items-center gap-2">
                <Users className="h-5 w-5 text-gray-500" />
                <span>{event.attendees.length} attendees</span>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default EventDashboard;
