import React, { useState, useEffect } from 'react';
import { Calendar, Clock, Users, Bell, ChevronRight, Plus, X, Check } from 'lucide-react';
import { useNavigate } from 'react-router-dom';
import { io } from 'socket.io-client';

const EventDashboard = () => {
  const [events, setEvents] = useState([]);
  const [showCreateModal, setShowCreateModal] = useState(false);
  const [notifications, setNotifications] = useState([]);
  const [newEvent, setNewEvent] = useState({
    title: '',
    date: '',
    time: '',
    attendees: []
  });
  const [socket, setSocket] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const newSocket = io('http://localhost:4000');
    setSocket(newSocket);

    newSocket.on('eventNotification', (notification) => {
      setNotifications(prev => [...prev, notification]);
    });

    newSocket.on('eventUpdated', (updatedEvent) => {
      setEvents(prev => 
        prev.map(event => 
          event.id === updatedEvent.id ? updatedEvent : event
        )
      );
    });

    newSocket.on('eventCreated', (newEvent) => {
      setEvents(prev => [...prev, newEvent]);
    });

    return () => newSocket.disconnect();
  }, []);

  const handleCreateEvent = () => {
    if (socket) {
      const eventData = {
        ...newEvent,
        id: Date.now(),
        attendees: newEvent.attendees.split(',').map(email => ({
          id: Date.now() + Math.random(),
          email: email.trim(),
          status: 'pending'
        })),
        notifications: []
      };
      socket.emit('createEvent', eventData);
      setShowCreateModal(false);
      setNewEvent({ title: '', date: '', time: '', attendees: '' });
    }
  };

  const handleAttendeeResponse = (eventId, attendeeId, status) => {
    if (socket) {
      socket.emit('updateAttendeeStatus', { eventId, attendeeId, status });
    }
  };

  return (
    <div className="p-6 max-w-6xl mx-auto relative">
      {/* Header with Create Button */}
      <div className="mb-8 flex justify-between items-center">
        <div>
          <h1 className="text-3xl font-bold mb-2">EventSync Dashboard</h1>
          <p className="text-gray-600">Manage your upcoming events and attendees</p>
        </div>
        <button 
          onClick={() => setShowCreateModal(true)}
          className="bg-blue-500 text-white px-4 py-2 rounded-lg flex items-center gap-2 hover:bg-blue-600"
        >
          <Plus className="h-5 w-5" />
          Create Event
        </button>
      </div>

      {/* Notifications Panel */}
      <div className="fixed top-4 right-4 w-80 space-y-2 z-50">
        {notifications.map((notification, index) => (
          <div 
            key={index}
            className="bg-white p-4 rounded-lg shadow-lg flex items-center justify-between"
          >
            <div className="flex items-center gap-2">
              <Bell className="h-4 w-4 text-blue-500" />
              <span>{notification.message}</span>
            </div>
            <button 
              onClick={() => setNotifications(prev => prev.filter((_, i) => i !== index))}
              className="text-gray-500 hover:text-gray-700"
            >
              <X className="h-4 w-4" />
            </button>
          </div>
        ))}
      </div>

      {/* Create Event Modal */}
      {showCreateModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white p-6 rounded-lg w-full max-w-md">
            <div className="flex justify-between items-center mb-4">
              <h2 className="text-xl font-bold">Create New Event</h2>
              <button onClick={() => setShowCreateModal(false)}>
                <X className="h-5 w-5" />
              </button>
            </div>
            <div className="space-y-4">
              <div>
                <label className="block text-sm font-medium mb-1">Event Title</label>
                <input
                  type="text"
                  value={newEvent.title}
                  onChange={(e) => setNewEvent(prev => ({ ...prev, title: e.target.value }))}
                  className="w-full p-2 border rounded"
                  placeholder="Enter event title"
                />
              </div>
              <div>
                <label className="block text-sm font-medium mb-1">Date</label>
                <input
                  type="date"
                  value={newEvent.date}
                  onChange={(e) => setNewEvent(prev => ({ ...prev, date: e.target.value }))}
                  className="w-full p-2 border rounded"
                />
              </div>
              <div>
                <label className="block text-sm font-medium mb-1">Time</label>
                <input
                  type="time"
                  value={newEvent.time}
                  onChange={(e) => setNewEvent(prev => ({ ...prev, time: e.target.value }))}
                  className="w-full p-2 border rounded"
                />
              </div>
              <div>
                <label className="block text-sm font-medium mb-1">Attendees (comma-separated emails)</label>
                <textarea
                  value={newEvent.attendees}
                  onChange={(e) => setNewEvent(prev => ({ ...prev, attendees: e.target.value }))}
                  className="w-full p-2 border rounded"
                  placeholder="email1@example.com, email2@example.com"
                  rows={3}
                />
              </div>
              <button
                onClick={handleCreateEvent}
                className="w-full bg-blue-500 text-white py-2 rounded hover:bg-blue-600"
              >
                Create Event
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Events List */}
      <div className="grid gap-4">
        {events.map(event => (
          <div key={event.id} className="p-4 bg-white rounded shadow-lg hover:shadow-xl transition-shadow">
            <div className="flex items-center justify-between">
              <h3 className="text-xl font-semibold">{event.title}</h3>
              <button onClick={() => navigate(`/event/${event.id}`)} className="p-2 hover:bg-gray-100 rounded-full">
                <ChevronRight className="h-5 w-5" />
              </button>
            </div>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mt-4">
              <div className="flex items-center gap-2">
                <Calendar className="h-5 w-5 text-gray-500" />
                <span>{event.date}</span>
              </div>
              <div className="flex items-center gap-2">
                <Clock className="h-5 w-5 text-gray-500" />
                <span>{event.time}</span>
              </div>
              <div className="flex items-center gap-2">
                <Users className="h-5 w-5 text-gray-500" />
                <span>{event.attendees.length} attendees</span>
              </div>
            </div>

            <div className="mt-4">
              <h4 className="font-semibold mb-2">Attendees</h4>
              <div className="grid gap-2">
                {event.attendees.map(attendee => (
                  <div key={attendee.id} className="flex items-center justify-between p-2 bg-gray-50 rounded">
                    <span>{attendee.email}</span>
                    <div className="flex gap-2">
                      <button
                        onClick={() => handleAttendeeResponse(event.id, attendee.id, 'confirmed')}
                        className={`p-1 rounded ${attendee.status === 'confirmed' ? 'bg-green-100 text-green-800' : 'bg-gray-100'}`}
                      >
                        <Check className="h-4 w-4" />
                      </button>
                      <button
                        onClick={() => handleAttendeeResponse(event.id, attendee.id, 'declined')}
                        className={`p-1 rounded ${attendee.status === 'declined' ? 'bg-red-100 text-red-800' : 'bg-gray-100'}`}
                      >
                        <X className="h-4 w-4" />
                      </button>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default EventDashboard;
